import { useContext } from 'react'
import { CartContext } from '../../contexts/cart.context'

import './checkout.style.scss'

const Checkout = () => {
    const { cartItems,increaseItem,decreaseItem,deleteItem} = useContext(CartContext)
    return (
        <div>
            {cartItems.map((item) => {
                const { name, id, price, quantity } = item

                const incrementHandler = () => {
                    increaseItem(id)
                }
                const decrementHandler = () => {
                    decreaseItem(id)
                }
                const deleteHandler = () => {
                   deleteItem(id)
                }
                return (
                    <div key={id}>
                        <h2>{name}</h2>
                        <span>{quantity}</span>
                        <br/>
                        <span onClick={incrementHandler}>increment</span>
                        <br/>
                        <span onClick={decrementHandler}>decrement</span>
                        <br/>
                        <span onClick={deleteHandler}>delete</span>
                    </div>
                )

            })}
        </div>
    )

}

export default Checkout
