import { useContext, Fragment } from 'react'

import { CategoriesContext } from '../../contexts/categories.context'

import ProductCard from '../../components/product-card/product-card.component'
import CategoryPreview from '../../components/category-preview/category-preview.component'

import './shop.style.scss'

const Shop = () => {
    const { categoriesMap, setCategoriesMap } = useContext(CategoriesContext)


    return (
        //categoriesMap的数据结构是一个对象，key：不同product的title，value：同一类product组成的数组
        //首先通过Object.keys取到title
        //再通过categoriesMap[title].map来渲染每个title下的product

        <div className="shop-container">
            {Object.keys(categoriesMap).map((title) => {
                const products = categoriesMap[title];
                <CategoryPreview products={products} title={title} />
            })}

        </div>


    )







}

export default Shop


