import './form-input.style.scss'

const FormInput = ({ label, inputOptons }) => {
    return (
        <div className="group">

        {/* className 使用模板字符串包囊三元表达式 注意其中三元表达式要整个包含在{}中 */}
            <label className={
                `${inputOptions.value.length ? `shrink` : ''}
                form-input-label`
            }
            >{label} </label>

            <input className="form-input" {...inputOptons}>
            </input>
        </div>
    )
}

export default FormInput


