import { initializeApp } from 'firebase/app';
import {
    getAuth,
    signInWithRedirect,
    signInWithPopup,
    GoogleAuthProvider
} from 'firebase/auth';
import {
    getFirestore,
    doc,
    getDoc,
    setDoc
} from 'firebase/firestore'


const firebaseConfig = {
    apiKey: "AIzaSyB61zj7EbA8v_-M2464HvCt14mvtv5Sc8k",
    authDomain: "crwn-clothing-yy.firebaseapp.com",
    projectId: "crwn-clothing-yy",
    storageBucket: "crwn-clothing-yy.appspot.com",
    messagingSenderId: "74735360103",
    appId: "1:74735360103:web:e9302b965098aa2f961cc2"
};

// Initialize Firebase
const firebaseApp = initializeApp(firebaseConfig);

const provider = new GoogleAuthProvider();
provider.setCustomParameters({
    prompt: "select_account"
});


export const auth = getAuth();
export const SignInWithGooglePopup = () => signInWithPopup(auth,provider) //用于登陆



export const db = getFirestore()

export const createUserDoocumentFromAuth = async (userAuth) => {
    const userDocRef = doc(db,'users',userAuth.id)
    console.log(userDocRef);

    const userSnapshot = await getDoc(userDocRef);
    console.log(userSnapshot);
}
