import './cart-item.style.scss'

const CartItem = ({ name, quantity, imgUrl }) => {
    return (
        <div className="cart-item-container">
            <img src={imgUrl} />
            <div className="item-details">
                <span>{name}</span>
                <span>{quantity}</span>s
            </div>
        </div>
    )
}

export default CartItem
