import {createContext,useState,useEffect} from 'react'


//辅助函数（高阶函数）把一些内置参数在一开始传好，从而组件使用函数的时候只需要传入更少的必要参数
//用于
const addCartItem = (cartItems,productToAdd) => {
    //判断cartItems是否已经包含produtToAdd
    //包含：quantity+1
    //不包含：add item
    //返回并设置新的cartItems
    const isItemExisting = cartItems.find((item) => {
         return item.id === productToAdd.id
    })
    if(isItemExisting){
        return  cartItems.map((item) => (
            item.id === productToAdd.id ? 
            {...item,quantity:item.quantity+1} : item
        ))
    }
    return [...cartItems,{...productToAdd,quantity:1}]
}

const increaseItemHelper = (cartItems,id) => {
    cartItems.map((item) => {
        if(item.id===id){
            return {...item,quantity:item.quantity+1}
        }else return item
    })
}

const decreaseItemHelper = (cartItems,id) => {
    cartItems.map((item) => {
        if(item.id===id){
            return {...item,quantity:item.quantity-1}
        }else return item
    })
}




export const CartContext = createContext({
    isCartOpen: false,
    setIsCartOpen: ()=>{},

    cartItems: [],
    setCartItems: ()=>{},

    addItemToCart:()=>{},
    removeItemFromCart:()=>{},

    cartCount : 0,
    setCartCount: ()=>{},

})

export const CartProvider = ({children}) => {
    const [isCartOpen,setIsCartOpen] = useState(false)
    const [cartItems,setCartItems] = useState([])
    const [cartCount,setCartCount] = useState(0)

    useEffect(() => {
        const newCartCount = cartItems.reduce((total,currentItem) => {return total+currentItem.quantity },0)
        setCartCount(newCartCount)
    },[cartItems])

    //用于在productCard向cart添加item
    const addItemToCart = (productToAdd) => {
        setCartItems(addCartItem(cartItems,productToAdd))
    }

    //用于在checkout中增加item数量
    const increaseItem = (id) => {
       setCartItems( increaseItemHelper(cartItems,id))
    }



    const value = {
        isCartOpen,setIsCartOpen,

        cartItems,setCartItems,

        addItemToCart,cartCount,

        increaseItem,decreaseItem

    }

    return <CartContext.Provider value={value}>{children}</CartContext.Provider>
}
