import {useContext,useState,useEffect} from 'react'
import {useParams} from 'react-router-dom'
import {CategoriesContext} from '../../contexts/categories.context'

import ProductCard from '../../components/product-card/product-card.component'

import './shop-category.style.scss'

const ShopCategory = () => {
    const productsCategory = useParams()
    const {categoriesMap} = useContext(CategoriesContext)

    const [products,setProducts] = useState(categoriesMap[productsCategory]) //safeguard

    useEffect(() => {
        setProducts(categoriesMap[productsCategory]);
    },[productsCategory,categoriesMap])

    return(
        <div className="category-container">
        {/* safeguard：短路语句 */}
        {products && products.map((product) => {
            return <ProductCard key={product.id} product={product}/>
        })}

        </div>
    )
}

export default ShopCategory
