import {useState} from 'react'

const SignUpForm = () => {
    //把整个表单的data作为一个整体写在一个state里面，就不需要写好几个useState，setState
    const defaultFormFields = [
        // displayName = '',
        email = '',
        passwprd = '',
        confirmPassword = '',
    ]
    const [formFields,setFormFields] = useState(defaultFormFields)
    const {displayName,email,passwprd,confirmPassword} = formFields

    const handleChange = (event) => {
        const {name,value} = event.target
        setFormFields({...formFields,[name]:value}) //展开数组formFields,并且更改里面某个key的值
    }

    return (
        <div>
        <h1>Sign up with your email and password</h1>

        <form>
        <label>displayName</label>
        <input type="text" required name="displayName" onChange={handleChange} value={1}></input>

        <label>email</label>
        <input type="text" required name="email" onChange={handleChange} value={email}></input>

        <label>password</label>
        <input type="text" required name="password" onChange={handleChange} value={passwprd}></input>

        <label>confirm your password</label>
        <input type="text" required name="confirmPassword" onChange={handleChange} value={confirmPassword}></input>

        <button>submit</button>
        </form>
        </div>

    )
}

export default SignUpForm
