    const { cartItems,addItemToCart} = useContext(CartContext)
    return (
        <div>
            {cartItems.map((item) => {
                const { name, id, price, quantity } = item

                const incrementHandler = () => {
                    addItemToCart(item)
                }
                const decrementHandler = () => {

                }
                return (
                    <div key={id}>
                        <h2>{name}</h2>
                        <span>{quantity}</span>
                        <br/>
                        <span onClick={incrementHandler}>increment</span>
                        <br/>
                        <span onClick={decrementHandler}>decrement</span>
                    </div>
                )

            })}
        </div>
    )

}

