import {createContext,useState} from 'react'

const addCartItem = (cartItems,produtToAdd) => {
    //判断cartItems是否已经包含produtToAdd
    for(let item of cartItems){
        if(item.id === produtToAdd.id){
            return setCartItems(cartItems,{item:quantity+1})
        }
    }
    return setCartItems(...cartItems,produtToAdd)

    //包含：quantity+1

    //不包含：add item

    //返回并设置新的cartItems
}


export const CartContext = createContext({
    isCartOpen: false,
    setIsCartOpen: ()=>{},

    cartItems: [],
    setCartItems: ()=>{},

    addCartItem:()=>{}
})

export const CartProvider = ({children}) => {
    const [isCartOpen,setIsCartOpen] = useState(false)
    const [cartItems,setCartItems] = useState([])


    const value = {isCartOpen,setIsCartOpen,cartItems,setCartItems,addCartItem}

    return <CartContext.Provider value={value}>{children}</CartContext.Provider>
}

