import {createContext,useState} from 'react'

const addCartItem = (cartItems,setCartItems,productToAdd) => {

    //判断cartItems是否已经包含produtToAdd
    //包含：quantity+1
    //不包含：add item
    //返回并设置新的cartItems

    const isItemExisting = cartItems.find((item) => {
         return item.id === productToAdd.id
    })
    let newCartItems = []
    if(isItemExisting){
        newCartItems =  cartItems.map((item) => (
            item.id === productToAdd.id ? 
            {...item,quantity:item.quantity+1} : item
        ))
    }else{newCartItems =  [...cartItems,{...productToAdd,quantity:1}]}
    setCartItems(newCartItems)
}

const count = (cartItems) => {
    return cartItems.reduce((sum,item) => {
        return sum + item.quantity
    },0)
}


export const CartContext = createContext({
    isCartOpen: false,
    setIsCartOpen: ()=>{},

    cartItems: [],
    setCartItems: ()=>{},
    quantitySum : 0,

    addCartItems:()=>{}
})

export const CartProvider = ({children}) => {
    const [isCartOpen,setIsCartOpen] = useState(false)
    const [cartItems,setCartItems] = useState([])

    const quantitySum = count(cartItems)

    const value = {isCartOpen,setIsCartOpen,cartItems,setCartItems,addCartItem,quantitySum}

    return <CartContext.Provider value={value}>{children}</CartContext.Provider>
}
